mapper.select:
    /*product.productTotal*/
		SELECT
			COUNT(*) AS TOTAL_CNT
		FROM
			PRODUCTINFO	
		WHERE
    		PRDTYPE = #{codeType}
		AND
    		PRDCTGR = #{prdCtgr, jdbcType=VARCHAR}

mapper.insert:
	/*product.productInfoInsert*/

			SELECT 
    				nvl(TO_NUMBER(MAX(PRDIDX)+1),0)
	 			FROM
	 				PRODUCTINFO 

		INSERT INTO
			PRODUCTINFO
			(
    			PRDIDX
    			,PRDCTGR
    			,PRDNAME
    			,PRDPRC
    			,PRDIMG
    			,PRDVIEW
    			,PRDTYPE
			)
    	VALUES
		(
    		#{prdIdx}
	  		,#{prdCtgr}
    		,#{prdName}
    		,#{prdPrc}
    		,#{prdImg, jdbcType=VARCHAR}
    		,#{prdView}
    		,#{codeType, jdbcType=VARCHAR}
    	)

mapper.update:
/*product.productInfoUpdate*/
	UPDATE
		PRODUCTINFO
	SET
		PRDNAME = #{prdName}
		,PRDPRC = #{prdPrc}
		,PRDVIEW = #{prdView}
	WHERE
		PRDIDX = #{prdIdx}

/*product.fileNameUpdate*/
	UPDATE
		PRODUCTINFO
	SET
		PRDIMG = #{prdImg}
	WHERE
		PRDIDX = #{prdIdx}
