mapper.update:
/*code.codeUpdate*/
	UPDATE
		CODE
	SET
		CODEDESC = #{codeDesc}
	WHERE
		CODETYPE = #{codeType}

mapper.insert:
    /*code.comcodeInsert*/
		INSERT INTO
			COMCODE
			(
    			CODEID
    			,CODETYPE
    			,CODENAME
    			,CODECOMMENT
			)
    	VALUES
		(
    		(
    			SELECT 
    				nvl(TO_NUMBER(MAX(CODEID)+1),0)
	 			FROM
	 				COMCODE
	  		)
    		,#{codeType}
    		,#{codeName}
    		,#{codeComment}
		)

mapper.update:
/*code.comcodeUpdate*/
	UPDATE
		COMCODE
	SET
		CODENAME = #{codeName}
		,CODECOMMENT = #{codeComment}
	WHERE
		CODEID = #{codeId}
