mapper.insert:
	/*schedule.insertSchedule*/
    	insert into
			schedule 			
		VALUES
		 (
    		(SELECT NVL(MAX(s_no)+1, 1)FROM schedule)
    		,#{c_tel}
    		,#{car_number}
    		,#{s_contents}
    		,0
    		,#{s_comment}
    		,to_date(#{s_date}, 'YYYY-MM-DD HH24')
    	 )

mapper.update:
/*schedule.scheduleUpdate*/
	update schedule

	set 
	S_CONTENTS = #{s_contents}
	,S_CHECK = #{s_check}
	,S_COMMENT = #{s_comment}
	,S_DATE = TO_DATE(#{s_date}, 'YYYY-MM-DD HH24')

	where S_NO = #{s_no}
